import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import "../../../styles/authTheme.css";

const API_URL = process.env.REACT_APP_API_URL;

function Login() {
  const navigate = useNavigate();
  const [step, setStep] = useState("password"); // "password", "send-code", "enter-code"
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [codeLogin, setCodeLogin] = useState("");
  const [errors, setErrors] = useState({ email: "", password: "", general: "", code: "" });
  const [showCheck, setShowCheck] = useState(false);
  const [loading, setLoading] = useState(false);

  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  useEffect(() => {
    setShowCheck(validateEmail(email));
  }, [email]);

  const handleLoginWithPassword = async (e) => {
    e.preventDefault();
    const newErrors = { email: "", password: "", general: "", code: "" };
    let isValid = true;

    if (!email.trim()) {
      newErrors.email = "Campo requerido";
      isValid = false;
    } else if (!validateEmail(email)) {
      newErrors.email = "Por favor, ingresa un email válido.";
      isValid = false;
    }

    if (!password.trim()) {
      newErrors.password = "Campo requerido";
      isValid = false;
    } else if (password.length < 6) {
      newErrors.password = "Tu contraseña debe tener al menos 6 caracteres.";
      isValid = false;
    }

    if (!isValid) return setErrors(newErrors);

    try {
      const response = await fetch(`${API_URL}/api/auth/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        setErrors((prev) => ({ ...prev, general: "Usuario y/o contraseña incorrectos." }));
        return;
      }

      const data = await response.json();
      const decoded = jwtDecode(data.token);
      localStorage.setItem("token", data.token);
      localStorage.setItem("userId", decoded.userId);

      if (decoded.role === "student") {
        navigate("/student/home");
      } else {
        alert("Tu cuenta no tiene permiso para acceder al perfil de estudiante.");
      }
    } catch (err) {
      alert("Login fallido. Verifica tus datos o conexión.");
    }
  };

  const handleSendCode = async (e) => {
    e.preventDefault();
    const newErrors = { email: "", password: "", general: "", code: "" };
    if (!email.trim()) {
      newErrors.email = "Campo requerido";
    } else if (!validateEmail(email)) {
      newErrors.email = "Por favor, ingresa un email válido.";
    }

    if (newErrors.email) return setErrors(newErrors);

    try {
      setLoading(true);
      const response = await fetch(`${API_URL}/api/auth/send-login-code`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });

      if (!response.ok) {
        setErrors((prev) => ({ ...prev, general: "Error al enviar el código de inicio de sesión." }));
        return;
      }

      alert("Se ha enviado un código de inicio de sesión a tu correo.");
      setStep("enter-code");
    } catch (err) {
      alert("Hubo un error al enviar el código.");
    } finally {
      setLoading(false);
    }
  };

  const handleLoginWithCode = async (e) => {
    e.preventDefault();
    const newErrors = { email: "", password: "", general: "", code: "" };
    if (!codeLogin.trim()) {
      newErrors.code = "Campo requerido";
      return setErrors(newErrors);
    }

    try {
      const response = await fetch(`${API_URL}/api/auth/login-with-code`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, code: codeLogin }),
      });

      if (!response.ok) {
        setErrors((prev) => ({ ...prev, general: "Código incorrecto o expirado" }));
        return;
      }

      const data = await response.json();
      const decoded = jwtDecode(data.token);
      localStorage.setItem("token", data.token);
      localStorage.setItem("userId", decoded.userId);

      if (decoded.role === "student") {
        navigate("/student/home");
      } else {
        alert("Tu cuenta no tiene permiso para acceder al perfil de estudiante.");
      }
    } catch (err) {
      alert("Login fallido. Verifica tus datos o conexión.");
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="auth-background" />
      <div className="auth-overlay" />
      <div className="auth-card">
        <div className="auth-logo" onClick={() => navigate("/")}>NutriScanU</div>

        <h1>Bienvenido de vuelta</h1>
        <p>Qué bueno verte otra vez :)</p>

        {step === "password" && (
          <form onSubmit={handleLoginWithPassword}>
            <label>Correo</label>
            <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} className={errors.email ? "input-error" : ""} />
            {errors.email && <div className="error-message">{errors.email}</div>}

            <label>Contraseña</label>
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className={errors.password ? "input-error" : ""} />
            {errors.password && <div className="error-message">{errors.password}</div>}

            {errors.general && <div className="error-message general-error">{errors.general}</div>}

            <button type="submit">Iniciar sesión</button>

            <div className="center-or"><span>O</span></div>
            <div className="login-code-button" onClick={() => setStep("send-code")}>Usar un código de inicio de sesión</div>
            <span className="forgot-password-link" onClick={() => navigate("/forgot-password")}>¿Olvidaste la contraseña?</span>
          </form>
        )}

        {step === "send-code" && (
          <form onSubmit={handleSendCode}>
            <label>Correo</label>
            <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} className={errors.email ? "input-error" : ""} />
            {errors.email && <div className="error-message">{errors.email}</div>}
            {errors.general && <div className="error-message general-error">{errors.general}</div>}

            <button type="submit" disabled={loading}>{loading ? "Enviando..." : "Enviar código"}</button>
            <div className="login-code-button" onClick={() => setStep("password")}>¿Usar contraseña?</div>
          </form>
        )}

        {step === "enter-code" && (
          <form onSubmit={handleLoginWithCode}>
            <label>Código de inicio de sesión</label>
            <input type="text" value={codeLogin} onChange={(e) => setCodeLogin(e.target.value)} className={errors.code ? "input-error" : ""} />
            {errors.code && <div className="error-message">{errors.code}</div>}
            {errors.general && <div className="error-message general-error">{errors.general}</div>}

            <button type="submit">Iniciar sesión</button>
            <div className="login-code-button" onClick={() => setStep("password")}>¿Usar contraseña?</div>
          </form>
        )}

        <div className="auth-footer">
          <text>¿Es la primera vez que usas NutriScanU?</text>
          <span onClick={() => navigate("/register")}> Regístrate</span>
        </div>
      </div>
    </div>
  );
}

export default Login;
