import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import "../../../styles/authTheme.css";

const API_URL = process.env.REACT_APP_API_URL;

function Login() {
  const navigate = useNavigate();
  const [step, setStep] = useState("default");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [code, setCode] = useState(["", "", "", "", ""]);
  const [errors, setErrors] = useState({ email: "", password: "", general: "", code: "" });
  const [showCheck, setShowCheck] = useState(false);
  const [loading, setLoading] = useState(false);
  const inputRefs = useRef([]);

  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  useEffect(() => {
    setShowCheck(validateEmail(email));
  }, [email]);

  const resetForm = () => {
    setEmail("");
    setPassword("");
    setCode(["", "", "", "", ""]);
    setErrors({ email: "", password: "", general: "", code: "" });
    setShowCheck(false);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const newErrors = { email: "", password: "", general: "", code: "" };
    let isValid = true;

    if (!email.trim()) {
      newErrors.email = "Campo requerido";
      isValid = false;
    } else if (!validateEmail(email)) {
      newErrors.email = "Por favor, ingresa un email válido.";
      isValid = false;
    }

    if (!password.trim()) {
      newErrors.password = "Campo requerido";
      isValid = false;
    } else if (password.length < 6) {
      newErrors.password = "Tu contraseña debe tener al menos 6 caracteres.";
      isValid = false;
    }

    if (!isValid) return setErrors(newErrors);

    try {
      const response = await fetch(`${API_URL}/api/auth/login`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        setErrors((prev) => ({ ...prev, general: "Usuario y/o contraseña incorrectos." }));
        return;
      }

      const data = await response.json();
      const decoded = jwtDecode(data.token);
      localStorage.setItem("token", data.token);
      localStorage.setItem("userId", decoded.userId);

      if (decoded.role === "student") {
        navigate("/student/home");
      } else {
        alert("Tu cuenta no tiene permiso para acceder al perfil de estudiante.");
      }
    } catch (err) {
      alert("Login fallido. Verifica tus datos o conexión.");
    }
  };

  const handleSendCode = async (e) => {
    e.preventDefault();
    if (!validateEmail(email)) {
      return setErrors({ ...errors, email: "Por favor, ingresa un email válido." });
    }

    try {
      setLoading(true);
      const res = await fetch(`${API_URL}/api/auth/send-login-code`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });

      if (!res.ok) return setErrors({ ...errors, general: "Error al enviar el código" });
      setStep("enter-code");
      setErrors({ email: "", password: "", general: "", code: "" });
    } catch (err) {
      alert("Error al enviar el código");
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitCode = async (e) => {
    e.preventDefault();
    if (code.some((digit) => digit.trim() === "")) {
      return setErrors({ ...errors, code: "Código incompleto" });
    }
    const finalCode = code.join("");

    try {
      const res = await fetch(`${API_URL}/api/auth/login-with-code`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, code: finalCode }),
      });

      if (!res.ok) return setErrors({ ...errors, general: "Código incorrecto o expirado" });

      const data = await res.json();
      const decoded = jwtDecode(data.token);
      localStorage.setItem("token", data.token);
      localStorage.setItem("userId", decoded.userId);

      if (decoded.role === "student") {
        navigate("/student/home");
      } else {
        alert("No tienes permiso para acceder.");
      }
    } catch (err) {
      alert("Error de red.");
    }
  };

  const handleCodeChange = (e, index) => {
    const val = e.target.value;
    if (!/^\d?$/.test(val)) return;
    const newCode = [...code];
    newCode[index] = val;
    setCode(newCode);
    if (val && index < 4) inputRefs.current[index + 1]?.focus();
  };

  const handlePasteCode = (e) => {
    e.preventDefault();
    const pastedData = e.clipboardData.getData("text").trim();
    if (/^\d{5}$/.test(pastedData)) {
      const digits = pastedData.split("");
      setCode(digits);
      inputRefs.current[4]?.focus();
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="auth-background" />
      <div className="auth-overlay" />
      <div className="auth-card">
        <div className="auth-logo" onClick={() => navigate("/")}>NutriScanU</div>

        {step === "default" && (
          <form onSubmit={handleLogin}>
            <h1>Bienvenido de vuelta</h1>
            <label htmlFor="email">Correo</label>
            <input
              id="email"
              type="text"
              placeholder="Ingresa tu correo"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className={errors.email ? "input-error" : ""}
            />
            {errors.email && <div className="error-message">{errors.email}</div>}

            <label htmlFor="password">Contraseña</label>
            <input
              id="password"
              type="password"
              placeholder="Ingresa tu contraseña"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className={errors.password ? "input-error" : ""}
            />
            {errors.password && <div className="error-message">{errors.password}</div>}
            {errors.general && <div className="error-message general-error">{errors.general}</div>}

            <button type="submit">Iniciar sesión</button>

            <div className="login-code-button" onClick={() => { resetForm(); setStep("send-code"); }}>Usar un código de inicio de sesión</div>
          </form>
        )}

        {step === "send-code" && (
          <form onSubmit={handleSendCode}>
            <h1>Iniciar sesión</h1>
            <label>Email o número de celular</label>
            <input
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className={errors.email ? "input-error" : ""}
            />
            {errors.email && <div className="error-message">{errors.email}</div>}
            {errors.general && <div className="error-message">{errors.general}</div>}

            <button type="submit" disabled={loading} className="login-button">
              {loading ? <div className="loader-spinner" /> : "Enviar código de inicio de sesión"}
            </button>

            <div className="login-code-button" onClick={() => { resetForm(); setStep("default"); }}>Usar contraseña</div>
          </form>
        )}

        {step === "enter-code" && (
          <form onSubmit={handleSubmitCode} onPaste={handlePasteCode}>
            <h2>Ingresa el código que te acabamos de enviar</h2>
            <p>Enviamos un código a <strong>{email}</strong>. Válido por 15 minutos.</p>
            <div className="code-inputs">
              {code.map((digit, i) => (
                <input
                  key={i}
                  type="text"
                  maxLength={1}
                  value={digit}
                  onChange={(e) => handleCodeChange(e, i)}
                  ref={(el) => (inputRefs.current[i] = el)}
                />
              ))}
            </div>
            {errors.code && <div className="error-message">{errors.code}</div>}
            {errors.general && <div className="error-message">{errors.general}</div>}

            <button type="submit">Iniciar sesión</button>
            <div className="login-code-button" onClick={() => { resetForm(); setStep("default"); }}>Usar contraseña</div>
            <div className="login-code-button secondary" onClick={() => handleSendCode({ preventDefault: () => {} })}>
  Reenviar código
</div>

          </form>
        )}

        <div className="auth-footer">
          <span>¿Es la primera vez que usas NutriScanU?</span>
          <span onClick={() => navigate("/register")}> Regístrate</span>
        </div>
      </div>
    </div>
  );
}

export default Login;
