import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import "../../../styles/authTheme.css";

const API_URL = process.env.REACT_APP_API_URL;

function Login() {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [codeLogin, setCodeLogin] = useState(""); // Para el código temporal
  const [isCodeLogin, setIsCodeLogin] = useState(false); // Determina si estamos en el flujo de código temporal
  const [errors, setErrors] = useState({
    email: "",
    password: "",
    general: "",
    code: "",
  });
  const [showCheck, setShowCheck] = useState(false);
  const [step, setStep] = useState("default"); // Controla la visibilidad del formulario

  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

  useEffect(() => {
    if (validateEmail(email)) {
      setShowCheck(true);
    } else {
      setShowCheck(false);
    }
  }, [email]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newErrors = { email: "", password: "", general: "", code: "" };
    let isValid = true;

    if (!email.trim()) {
      newErrors.email = "Campo requerido";
      isValid = false;
    } else if (!validateEmail(email)) {
      newErrors.email = "Por favor, ingresa un email válido.";
      isValid = false;
    }

    if (!password.trim() && !isCodeLogin) {
      newErrors.password = "Campo requerido";
      isValid = false;
    } else if (password.length < 6 && !isCodeLogin) {
      newErrors.password = "Tu contraseña debe tener al menos 6 caracteres.";
      isValid = false;
    }

    if (isCodeLogin && !codeLogin.trim()) {
      newErrors.code = "Campo requerido";
      isValid = false;
    }

    if (!isValid) {
      setErrors(newErrors);
      return;
    }

    try {
      let response;
      if (isCodeLogin) {
        response = await fetch(`${API_URL}/api/auth/login-with-code`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ email, code: codeLogin }),
        });
      } else {
        response = await fetch(`${API_URL}/api/auth/login`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ email, password }),
        });
      }

      if (!response.ok) {
        setErrors((prev) => ({
          ...prev,
          general: "Usuario y/o contraseña incorrectos.",
        }));
        return;
      }

      const data = await response.json();
      const decoded = jwtDecode(data.token);

      localStorage.setItem("token", data.token);
      localStorage.setItem("userId", decoded.userId);

      if (decoded.role === "student") {
        navigate("/student/home");
      } else {
        alert("Tu cuenta no tiene permiso para acceder al perfil de estudiante.");
      }
    } catch (err) {
      console.error("❌ Error:", err);
      alert("Login fallido. Verifica tus datos o conexión.");
    }
  };

  const handleSendCode = async (e) => {
    e.preventDefault();
    setErrors({});
    if (!validateEmail(email)) return setErrors({ email: "Correo inválido" });
    setStep("send-code"); // Cambiar a la vista para ingresar solo el correo y enviar el código

    try {
      const res = await fetch(`${API_URL}/api/auth/send-login-code`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });

      if (!res.ok) return setErrors({ general: "Error al enviar el código" });
    } catch (err) {
      alert("Error de red.");
    }
  };

  const handleCodeLogin = async () => {
    try {
      const response = await fetch(`${API_URL}/api/auth/send-login-code`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });

      if (!response.ok) {
        setErrors((prev) => ({
          ...prev,
          general: "Error al enviar el código de inicio de sesión.",
        }));
        return;
      }

      alert("Se ha enviado un código de inicio de sesión a tu correo.");
      setStep("enter-code"); // Cambiar el estado a mostrar el formulario para ingresar el código
    } catch (err) {
      console.error("❌ Error:", err);
      alert("Hubo un error al enviar el código.");
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="auth-background" />
      <div className="auth-overlay" />
      <div className="auth-card">
        <div className="auth-logo" onClick={() => navigate("/")}>NutriScanU</div>

        {step === "default" && (
          <>
            <h1>Bienvenido de vuelta</h1>
            <p>Qué bueno verte otra vez :)</p>
            <form onSubmit={handleSubmit}>
              <label>Correo</label>
              <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
              {errors.email && <div className="error-message">{errors.email}</div>}

              <label>Contraseña</label>
              <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
              {errors.password && <div className="error-message">{errors.password}</div>}
              {errors.general && <div className="error-message">{errors.general}</div>}

              <button type="submit">Iniciar sesión</button>

              <div className="center-or"><span>O</span></div>
              <div className="login-code-button" onClick={handleSendCode}>Usar un código de inicio de sesión</div>
            </form>

            <span className="forgot-password-link" onClick={() => navigate("/forgot-password")}>¿Olvidaste la contraseña?</span>
          </>
        )}

        {step === "send-code" && (
          <>
            <h1>Ingresa tu correo</h1>
            <p>Te enviaremos un código para que accedas sin contraseña.</p>
            <form onSubmit={handleCodeLogin}>
              <label>Correo electrónico</label>
              <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
              {errors.email && <div className="error-message">{errors.email}</div>}
              {errors.general && <div className="error-message">{errors.general}</div>}
              <button type="submit">Enviar código</button>
            </form>
            <div className="login-code-button" onClick={() => setStep("default")}>¿Usar contraseña?</div>
          </>
        )}

        {step === "enter-code" && (
          <>
            <h1>Ingresa el código que te acabamos de enviar</h1>
            <p>Enviamos un código a <strong>{email}</strong>. Válido por 15 minutos.</p>
            <form onSubmit={handleSubmitCode}>
              <div className="code-inputs">
                {code.map((c, i) => (
                  <input
                    key={i}
                    type="text"
                    maxLength={1}
                    value={c}
                    onChange={(e) => handleCodeChange(e, i)}
                    ref={(el) => inputRefs.current[i] = el}
                  />
                ))}
              </div>
              {errors.code && <div className="error-message">{errors.code}</div>}
              {errors.general && <div className="error-message">{errors.general}</div>}
              <button type="submit">Iniciar sesión</button>
            </form>
            <div className="login-code-button" onClick={() => setStep("default")}>Usar contraseña</div>
            <div className="login-code-button" onClick={handleResendCode}>Reenviar código</div>
          </>
        )}

        <div className="auth-footer">
          <text>¿Es la primera vez que usas NutriScanU?</text>
          <span onClick={() => navigate("/register")}> Regístrate</span>
        </div>
      </div>
    </div>
  );
}

export default Login;
